import React, {Component} from 'react';
import {Button, Form, Container} from "react-bootstrap";
import axios from 'axios';
import Dokumenty from '../Pages/Dokumenty'

class Login extends Component {
    constructor(props) {
        super(props);
        this.state = {
            login: null,
            haslo: null,
            loginfo : null,
            renderlog: false,
        };
    }
    handleSubmin = (event) => {
        event.preventDefault();
    }
    handleChangeLogin = (event) => {
        event.preventDefault();
        this.setState(
            {login: event.target.value}
        )
    }
    handleChangeHaslo = (event) => {
        event.preventDefault();
        this.setState(
            {haslo: event.target.value}
        )
    }
    logClick = () => {
        axios.get(`http://localhost:8081/pracownik/log?login=${this.state.login}&haslo=${this.state.haslo}`).then((response) => {
            this.setState({loginfo: response.data})
            this.setState({renderlog : true});
        })

    }
    render() {
        return (
            <>
                { this.state.loginfo ? this.state.renderlog && <Header login={true}/> : ''}
            <Container>
            <div className="login-in-page">
                <p>{this.state.login}</p>
                <p>{this.state.haslo}</p>
                <Form onSubmit={this.handleSubmit} className="log">
                    <Form.Label>Login</Form.Label>
                    <Form.Control type="input" placeholder="Login" name="login" onChange={this.handleChangeLogin}/>
                    <Form.Label>Haslo</Form.Label>
                    <Form.Control type="input" placeholder="Haslo" name="haslo" onChange={this.handleChangeHaslo} />
                    <Button variant="outline-info" type="submit" className="log mt-3" onClick={() => this.logClick()}> LogIn </Button>
                </Form>
                { this.state.loginfo ? this.state.renderlog && <Dokumenty login={true}/> : ''}
            </div>
            </Container>
                </>
        );
    }
}

export default Login;
