import React, {Component} from 'react';
import {Button,  Form, Container, Table} from "react-bootstrap";
import axios from 'axios';

class Home extends Component {
    constructor(props) {
        super(props);
        this.state={
            login : false,
            dok : [],
            optionurl : '',
            data1 : null,
            data2 : null
        }

    }
    componentDidMount() {
        axios({
            method: 'get',
            url: this.state.optionurl,
        }).then((response) => {
            this.setState({doks: response.data})
            console.log(response.data)

        }).catch((error) => {
            console.log(error)
        });
    }
    handleChangeData1 = (event) => {
        this.setState(
            {data1: event.target.value}
        )
    }
    handleChangeData2 = (event) => {
        this.setState(
            {data2: event.target.value}
        )
    }
    logClick = event => {
        axios.get(`http://localhost:8081/doks/datasort?data1=${this.state.login}&data2=${this.state.haslo}`).then((response) => {
            this.setState({loginfo: response.data})
            localStorage.setItem('doks' , this.state.doks)
        })


    }


    render() {
        if (this.props.login > 0)
            return (
                <Container className="data-in-page">
                        <Form action={""} method="get" className="dokdate">
                        <Table>
                            <td>
                                <Form.Control className="mt-3" type="datetime-local" placeholder="Enter Data" name="data1" onChange={this.handleChangeData1}/>
                            </td>
                            <td>
                                <Form.Control className="mt-3" type="datetime-local" placeholder="Enter Data" name="data2" onChange={this.handleChangeData2}/>
                            </td>
                            <td>
                                <Button variant="outline-info" type="submit" className="dokdate mt-3"> Ok </Button>
                            </td>
                        </Table>
                        </Form>
                    <p> {localStorage.getItem('doks')}</p>
                </Container>

            );
        else
            return (
                <div className="data-in-page"> <h2 class="text-danger">Login error</h2></div>
            );
    }
}

export default Home;
