import React, {Component} from 'react';
import {Button, Form} from "react-bootstrap";
import axios from 'axios';

class MagazynCud extends Component {
    constructor(props) {
        super(props);
        this.state = {
            adres : [],
            stan : [],
            mag :{}
        };
    }
    componentDidMount() {
        axios({
            method: 'get',
            url: `http://localhost:8081/mag/${this.props.id}`,
        }).then((response) => {
            this.setState({firma: response.data})
            console.log(response.data)

        }).catch((error) => {
            console.log(error)
        });
        axios({
            method: 'get',
            url: `http://localhost:8081/adres/all`,
        }).then((response) => {
            this.setState({adres: response.data})
            console.log(response.data)

        }).catch((error) => {
            console.log(error)
        });

        axios({
            method: 'get',
            url: `http://localhost:8081/stanmag/all`,
        }).then((response) => {
            this.setState({adres: response.data})
            console.log(response.data)

        }).catch((error) => {
            console.log(error)
        });
    }
    render() {
     if(this.props.typ === "dtails")
            return (
                <>
                    <tr className="bg-light">

                        <th> </th>
                        <th> Towar </th>
                        <th> Numer serijny</th>
                        <th> Ilosc </th>
                        <th></th>

                    </tr>
                    {
                        this.state.stanmag.map((element: any) => (
                            this.props.id === element.idMag ?
                                <tr className="bg-light">
                                    <td></td>
                                    <td>{element.idTow}</td>
                                    <td>{element.serTow}</td>
                                    <td>{element.ilTow}</td>
                                    <td></td>
                                </tr>
                                : ''

                        ) )
                    }
                </>
            );
    }
}

export default MagazynCud;
