import React, {Component} from 'react';
import {Button, Form} from "react-bootstrap";
import axios from 'axios';

class FirmyCud extends Component {
    constructor(props) {
        super(props);
        this.state = {
            adres : [],
            firma :{}
        };
    }
    componentDidMount() {
        axios({
            method: 'get',
            url: `http://localhost:8081/firmy/${this.props.id}`,
        }).then((response) => {
            this.setState({firma: response.data})
            console.log(response.data)

        }).catch((error) => {
            console.log(error)
        });
        axios({
            method: 'get',
            url: `http://localhost:8081/adres/all`,
        }).then((response) => {
            this.setState({adres: response.data})
            console.log(response.data)

        }).catch((error) => {
            console.log(error)
        });
    }
    render() {
        return (
            <div>
                Hello {this.props.typ}
            </div>
        );
    }
}

export default FirmyCud;
