import React, {Component} from 'react';
import {Button, Table,} from "react-bootstrap";
import AdresCud from './AdresCud'
import axios from 'axios';

class Adres extends Component {
    constructor(props) {
        super(props);
        this.state = {
            adres : [],
            idClick : -1,
            render: false,
            typ : ""
        };
    }
    add(id, typ)
    {
        this.state.idClick = id;
        this.state.typ = typ;
        this.setState({render : !this.state.render})
    }
    componentDidMount() {
        axios({
            method: 'get',
            url: 'http://localhost:8081/adres/all',
        }).then((response) => {
            this.setState({adres: response.data})
            console.log(response.data)

        }).catch((error) => {
            console.log(error)
        });
    }

    render() {
        return (
            <Table>
                <thead>
                <tr>
                    <th> ID Adresy </th>
                    <th> Ulica </th>
                    <th> Budynek </th>
                    <th> Mejscowosc </th>
                    <th> Kod Pocztowy </th>
                    <th> Kraj </th>
                    <th colSpan="2"><Button variant="outline-info" onClick={() => this.add(-1, "add")}>Dodaj Adres</Button></th>
                </tr>
                </thead>
                <tbody>
                { this.state.typ==="add" ? this.state.render && <AdresCud typ={"add"}/> : '' }
                {
                    this.state.adres.map((element: any) => (
                        <>
                        <tr>
                            <td>{element.idAd}</td>
                            <td>{element.ulica}</td>
                            <td>{element.bud}</td>
                            <td>{element.mej}</td>
                            <td>{element.kod}</td>
                            <td>{element.kraj}</td>
                            <td><Button variant="outline-info" onClick={() => this.add(element.idAd, "update")}>Edytuj</Button></td>
                            <td><Button variant="outline-info" onClick={() => this.add(element.idAd, "delete")}>Usun</Button></td>
                        </tr>
                            { this.state.idClick === element.idDok && this.state.typ==="details" ? this.state.render && <Details id={element.idDok} typ={this.props.typ}/> : '' }
                            { this.state.idClick === element.idDok && this.state.typ==="update" ? this.state.render && <Edycja id={element.idDok} typ={this.props.typ}/> : '' }
                            { this.state.idClick === element.idDok && this.state.typ==="delete" ? this.state.render && <Usun id={element.idDok} typ={this.props.typ}/> : '' }
                        </>
                    ) )
                }
                </tbody>
            </Table>
        );
    }
}

export default Adres;
