import React, {useState, Component} from 'react';
import {Button, Container, Form, Nav, Navbar, Modal} from "react-bootstrap";

import logo from './logo.png'
import {BrowserRouter as Router, Switch, Route} from "react-router-dom";
import Home from "../Pages/Home";
import Dokumenty from "../Pages/Dokumenty";
import Dane from "../Pages/Dane";

class Header extends Component() {
    constructor(props) {
        super(props);
        this.state = {
            show: false,
        };
    }
    setShow(param){
        this.state.show = param;
    }
    handleClose = () => this.setShow(false);
    handleShow = () => this.setShow(true);

    render() {
        return (
            <>
                <Navbar fixed="top" collapseOnSelect expand="md" bg="dark" variant="dark">
                    <Container>
                        <Navbar.Brand href="/">
                            <img
                                src={logo}
                                height="30"
                                width="30"
                                className="d-inline-block align-top"
                                alt="logo"
                            /> Magazyn
                        </Navbar.Brand>
                        <Navbar.Toggle aria-controls="responsive-navbar-nav"/>
                        <Navbar.Collapse id="responsive-navbar-nav">
                            <Nav className="mr-auto">
                                <Nav.Link href="/dokumenty">Dokumenty</Nav.Link>
                                <Nav.Link href="/dane">Dane</Nav.Link>
                            </Nav>
                            <Form inline>
                                <Button variant="outline-info" onClick={this.handleShow}>Logowanie</Button>
                            </Form>
                        </Navbar.Collapse>
                    </Container>
                </Navbar>
                <Router>
                    <Switch>
                        <Route exact path="/" children={<Home login={true}/>}/>
                        <Route exact path="/dokumenty" component={Dokumenty}/>
                        <Route exact path="/dane" component={Dane}/>
                    </Switch>
                </Router>
                <Modal show={this.state.show} onHide={this.handleClose}>
                    <Modal.Header closeButton>
                        <Modal.Title>
                            Logowanie
                        </Modal.Title>
                    </Modal.Header>
                    <Modal.Body>
                        <Form id={"logowanie"}>
                            <div className="mx-5">
                                <Form.Group controlId="">
                                    <Form.Label>Login:</Form.Label>
                                    <Form.Control type="text" placeholder="Wpisz login" name="login"></Form.Control>
                                </Form.Group>
                                <Form.Group controlId="">
                                    <Form.Label>Haslo:</Form.Label>
                                    <Form.Control type="password" placeholder="Wpisz haslo" name="haslo"></Form.Control>
                                </Form.Group>
                                <Button variant="outline-info" type="submit" className="mt-3">LogIn</Button>
                            </div>
                        </Form>
                    </Modal.Body>
                </Modal>
            </>
        )
    }
}
export default Header;
