import React, {Component} from 'react';
import {Button, Form} from "react-bootstrap";
import axios from 'axios';

class MagazynCud extends Component {
    constructor(props) {
        super(props);
        this.state = {
            adres : [],
            stan : [],
            mag :{}
        };
    }
    componentDidMount() {
        axios({
            method: 'get',
            url: `http://localhost:8081/mag/${this.props.id}`,
        }).then((response) => {
            this.setState({firma: response.data})
            console.log(response.data)

        }).catch((error) => {
            console.log(error)
        });
        axios({
            method: 'get',
            url: `http://localhost:8081/adres/all`,
        }).then((response) => {
            this.setState({adres: response.data})
            console.log(response.data)

        }).catch((error) => {
            console.log(error)
        });

        axios({
            method: 'get',
            url: `http://localhost:8081/stanmag/all`,
        }).then((response) => {
            this.setState({adres: response.data})
            console.log(response.data)

        }).catch((error) => {
            console.log(error)
        });
    }
    render() {
     if(this.props.typ === "dtails")
            return (
                <tr className="bg-light">
                    <td><Form.Control as="input" name="id" defaultValue={this.props.id}  readOnly  /></td>
                    <td colSpan={5} className="text-danger"> Czy na pewno chcesz usunąć firme?</td>
                    <td><Button variant="outline-info" type="submit" className="firmycud"> Ok </Button></td>
                </tr>
            );
    }
}

export default MagazynCud;
