import React, {Component} from 'react';

class Konto extends Component {
    constructor(props){
        super(props);
        this.state = {
            data : null,
        };
    }
dateClick(){
    this.setState({data: new Date().getFullYear()})
    console.log(new Date())
    axios({
        method: 'post',
        url: 'http://localhost:8081/history/add',
        data: {
            firstName: 'Finn',
            lastName: 'Williams'
        }
    });
}

    render() {
        return (
            <div className="data-in-page">
                {localStorage.getItem('loginfo')}
                <p onClick={() => this.dateClick()}>click</p>
                {this.state.data}
            </div>
        );
    }
}

export default Konto;
