import React, {Component} from 'react';
import {Button, Form} from "react-bootstrap";
import axios from 'axios';
import PozycjaCud from '../Dokumenty/PozycjaCud'

class DokumentyCud extends Component {
    constructor(props) {
        super(props);
        this.state = {
            doks : [],
            doki : [],
            dok :{}
        };
    }
    pozycjaClick(id, typ)
    {
        this.setState({idClick: id});
        this.setState({typ: typ});
        this.setState({render : !this.state.render});
    }
    componentDidMount() {
        axios({
            method: 'get',
            url: `http://localhost:8081/dok/${this.props.id}`,
        }).then((response) => {
            this.setState({dok: response.data})
            console.log(response.data)

        }).catch((error) => {
            console.log(error)
        });
        axios({
            method: 'get',
            url: `http://localhost:8081/doki/${this.props.id}`,
        }).then((response) => {
            this.setState({doki: response.data})
            console.log(response.data)

        }).catch((error) => {
            console.log(error)
        });

        axios({
            method: 'get',
            url: `http://localhost:8081/doks/all`,
        }).then((response) => {
            this.setState({doks: response.data})
            console.log(response.data)

        }).catch((error) => {
            console.log(error)
        });
    }
    render() {
        if(this.props.typ === "details")
        return (<>
                <tr className="bg-light">

                    <th> </th>
                    <th> No </th>
                    <th> Towar </th>
                    <th> Numer serijny</th>
                    <th> Ilosc </th>
                    <th colSpan={2}><Button variant="outline-info" onClick={() => this.pozycjaClick(-1, "add")}>Dodaj Pozycje</Button></th>
                </tr>
                { this.state.typ==="add" ? this.state.render && <PozycjaCud typ={this.props.typ} id={this.props.id}/> : '' }
                {
                    this.state.doki.map((element: any) => (
                        this.props.id === element.idDok ?
                            <tr className="bg-light">
                                <td></td>
                                <td>{element.lpoz}</td>
                                <td>{element.idTow}</td>
                                <td>{element.serTow}</td>
                                <td>{element.ilTow}</td>
                                <td><Button variant="outline-info">Edytuj</Button></td>
                                <td><Button variant="outline-info">Usun</Button></td>
                            </tr>
                            : ''

                    ) )
                }
            </>
        );
        else
        return (
            <div>
                {this.props.typ}
            </div>
        );
    }
}

export default DokumentyCud;
