import React, {Component} from 'react';
import './Pages.css';
import TabView from '../Components/TabView'
import {Container} from "react-bootstrap";

class Dokumenty extends Component {
    render() {
        return (

                <Container className="data-in-page">
                    <Form action={"http://localhost:8081/doks"+this.state.typ} method="post" className="dokscud">
                        <Table className="table">
                            <thead>
                            <>
                                <tr>
                                    <th>No</th>
                                    <th><Button variant="outline-info" onClick={() => this.sort()}>Magazyn</Button></th>
                                    <th>Typ Dokumentu</th>
                                    <th>No Dokumentu</th>
                                    <th>Firma</th>
                                    <th >Data</th>

                                    <th></th>
                                    <th colSpan="2"><Button variant="outline-info" onClick={() => this.add(-1, "add")}>Dodaj Dokument</Button></th>
                                </tr>
                                {this.state.render}
                            </>
                            </thead>
                            <tbody >
                            { this.state.typ==="add" ? this.state.render && <Dodawanie typ={this.props.typ}/> : '' }
                            {
                                this.state.dokmag.map((element: any) => (
                                    <>
                                        <tr>
                                            <td>{element.idDok}</td>
                                            <td>{element.idMag}</td>
                                            <td>{element.tdok}</td>
                                            <td>{element.ndok}</td>
                                            <td>{element.idF}</td>
                                            <td>{Moment(element.data).format("DD-MM-YYYY hh:mm")}</td>
                                            <td><Button variant="outline-info" onClick={() => this.add(element.idDok, "details")}>Details</Button></td>
                                            <td><Button variant="outline-info" onClick={() => this.add(element.idDok, "update")}>Edytuj</Button></td>
                                            <td><Button variant="outline-info" onClick={() => this.add(element.idDok, "delete")}>Usun</Button></td>

                                        </tr>
                                        { this.state.idClick === element.idDok && this.state.typ==="details" ? this.state.render && <Details id={element.idDok} typ={this.props.typ}/> : '' }
                                        { this.state.idClick === element.idDok && this.state.typ==="update" ? this.state.render && <Edycja id={element.idDok} typ={this.props.typ}/> : '' }
                                        { this.state.idClick === element.idDok && this.state.typ==="delete" ? this.state.render && <Usun id={element.idDok} typ={this.props.typ}/> : '' }
                                    </>

                                ) )
                            }
                            </tbody>
                        </Table>
                    </Form>
                </Container>


        );
    }
}

export default Dokumenty;
