import React, {Component} from 'react';
import {Button, Form} from "react-bootstrap";
import axios from 'axios';


class PozycjaCud extends Component {
    constructor(props) {
        super(props);
        this.state = {
            tow : [],
            poz :{}
        };
    }
    componentDidMount() {
        axios({
            method: 'get',
            url: `http://localhost:8081/doki/${this.props.id}/${this.props.poz}`,
        }).then((response) => {
            this.setState({poz: response.data})
            console.log(response.data)

        }).catch((error) => {
            console.log(error)
        });
        axios({
            method: 'get',
            url: `http://localhost:8081/tow/all`,
        }).then((response) => {
            this.setState({tow: response.data})
            console.log(response.data)

        }).catch((error) => {
            console.log(error)
        });
    }
      
    render() {
         if(this.props.typ === "add")
                return (
                    <tr className="bg-light">
                        <td></td>
                        <td><Form.Control as="input" placeholder="Nazwa" name="nMag" /></td>
                        <td>
                            <Form.Control as="select"  name="idAd">
                                {
                                    this.state.adres.map((element: any) => (
                                        <option value={element.idAd}>{element.ulica} {element.bud} {element.mej} {element.kraj}</option>
                                    ))
                                }
                            </Form.Control>
                        </td>
                        <td><Form.Control as="input" placeholder="Kierownik magazynu" name="kMag"/></td>
                        <td><Form.Control as="input" placeholder="Telefon" name="tMag"/></td>
                        <td><Button variant="outline-info" type="submit" className="magcud"> Dodaj </Button></td>
                    </tr>
                );
        
        else
        return (
            <div>
                {this.props.typ}
            </div>
        );
    }
}

export default PozycjaCud;
