import React, {Component} from 'react';
import {Table, Button, Form} from "react-bootstrap";
import Moment from 'moment';
import axios from 'axios';
import Details from "../Components/Details";
import Edycja from "../Components/Edycja"
import Dodawanie from "../Components/Dodawawie"
import Usun from "../Components/Usun"

class TabView extends Component {
    constructor(props) {
        super(props);
        this.state = {
            mags : [],
            dokmag: [],
            dok : [],
            tow :[],
            firm : [],
            adres : [],
            stanmag: [],
            idClick : -1,
            render: false,
            typ : ""
        };
    }
    smallestToBiggest(a, b) {
        return b.idDok - a.idDok;
    }

    biggestToSmallest(a, b) {
        return b.data - a.data;
    }
    add(id, typ)
    {
        this.state.idClick = id;
        this.state.typ = typ;
        this.setState({render : !this.state.render})
    }
    sort(){
        this.state.dokmag.sort((a,b) => b.data + a.data );
        this.setState({render : !this.state.render})
        console.log(this.state.dokmag)

    }
    componentDidMount() {
        axios({
            method: 'get',
            url: 'http://localhost:8081/adres/all',
        }).then((response) => {
            this.setState({adres: response.data})
            console.log(response.data)

        }).catch((error) => {
            console.log(error)
        });
        axios({
            method: 'get',
            url: 'http://localhost:8081/stanmag/all',
        }).then((response) => {
            this.setState({stanmag: response.data})
            console.log(response.data)

        }).catch((error) => {
            console.log(error)
        });
        axios({
            method: 'get',
            url: 'http://localhost:8081/doks/all',
        }).then((response) => {
            this.setState({dokmag: response.data})
            console.log(response.data)

        }).catch((error) => {
            console.log(error)
        });

        axios({
            method: 'get',
            url: 'http://localhost:8081/firmy/all',
        }).then((response) => {
            this.setState({firm: response.data})
            console.log(response.data)

        }).catch((error) => {
            console.log(error)
        });

        axios({
            method: 'get',
            url: 'http://localhost:8081/tow/all',
        }).then((response) => {
            this.setState({tow: response.data})
            console.log(response.data)

        }).catch((error) => {
            console.log(error)
        });
        axios({
            method: 'get',
            url: 'http://localhost:8081//doks/details/all',
        }).then((response) => {
            this.setState({dok: response.data})
            console.log(response.data)

        }).catch((error) => {
            console.log(error)
        });
        axios({
            method: 'get',
            url: 'http://localhost:8081/mag/all',
        }).then((response) => {
            this.setState({mags: response.data})
            console.log(response.data)

        }).catch((error) => {
            console.log(error)
        });
    }
    render() {
        if(this.props.typ === "dok" )
            return (<>
                    <tr className="bg-light">

                        <th> </th>
                        <th> No </th>
                        <th> Towar </th>
                        <th> Numer serijny</th>
                        <th> Ilosc </th>
                        <th colSpan={2}><Button variant="outline-info" onClick={() => this.add(-1, "add")}>Dodaj Pozycje</Button></th>
                    </tr>
                    { this.state.typ==="add" ? this.state.render && <Dodawanie typ={this.props.typ} id={this.props.id}/> : '' }
                    {
                        this.state.dok.map((element: any) => (
                            this.props.id === element.idDok ?
                                <tr className="bg-light">
                                    <td></td>
                                    <td>{element.lpoz}</td>
                                    <td>{element.idTow}</td>
                                    <td>{element.serTow}</td>
                                    <td>{element.ilTow}</td>
                                    <td><Button variant="outline-info">Edytuj</Button></td>
                                    <td><Button variant="outline-info">Usun</Button></td>
                                </tr>
                                : ''

                        ) )
                    }
                </>
            );
        else if(this.props.typ === "doks")
        return (
            <Form action={"http://localhost:8081/" + this.props.typ + "/"+this.state.typ} method="post" className="dane">
            <Table className="table">
                <thead>
                <>
                <tr>
                    <th>No</th>
                    <th>Magazyn</th>
                    <th>Typ Dokumentu</th>
                    <th>No Dokumentu</th>
                    <th>Firma</th>
                    <th ><Button variant="outline-info" onClick={() => this.sort()}>Data</Button></th>

                    <th></th>
                    <th colSpan="2"><Button variant="outline-info" onClick={() => this.add(-1, "add")}>Dodaj Dokument</Button></th>
                    </tr>
                {this.state.render}
                </>
                </thead>
                <tbody >
                { this.state.typ==="add" ? this.state.render && <Dodawanie typ={this.props.typ}/> : '' }
                {
                    this.state.dokmag.map((element: any) => (
                        <>
                        <tr>
                            <td>{element.idDok}</td>
                            <td>{element.idMag}</td>
                            <td>{element.tdok}</td>
                            <td>{element.ndok}</td>
                            <td>{element.idF}</td>
                            <td>{Moment(element.data).format("DD-MM-YYYY hh:mm")}</td>
                            <td><Button variant="outline-info" onClick={() => this.add(element.idDok, "details")}>Details</Button></td>
                            <td><Button variant="outline-info" onClick={() => this.add(element.idDok, "update")}>Edytuj</Button></td>
                            <td><Button variant="outline-info" onClick={() => this.add(element.idDok, "delete")}>Usun</Button></td>

                        </tr>
                          { this.state.idClick === element.idDok && this.state.typ==="details" ? this.state.render && <Details id={element.idDok} typ={this.props.typ}/> : '' }
                          { this.state.idClick === element.idDok && this.state.typ==="update" ? this.state.render && <Edycja id={element.idDok} typ={this.props.typ}/> : '' }
                          { this.state.idClick === element.idDok && this.state.typ==="delete" ? this.state.render && <Usun id={element.idDok} typ={this.props.typ}/> : '' }
                        </>

                    ) )
                }
                </tbody>
            </Table>
        </Form>
        );

        else if(this.props.typ === "tow")
            return (
                <Table>
                    <thead>
                    <tr>
                        <th> ID Towaru</th>
                        <th> Nazwa</th>
                        <th> Kod kreskowy </th>
                        <th> Producent </th>
                        <th> Waga(kg)</th>
                        <th>Wymiary(cm)</th>
                        <th colSpan="2"><Button variant="outline-info" onClick={() => this.add(-1, "dodaj")}>Dodaj Towar</Button></th>
                    </tr>
                    </thead>
                    <tbody>
                    { this.state.typ==="dodaj" ? this.state.render && <Dodawanie typ={this.props.typ}/> : '' }
                    {
                        this.state.tow.map((element: any) => (
                            <tr>
                                <td>{element.idTow}</td>
                                <td>{element.ntow}</td>
                                <td>{element.ktow}</td>
                                <td>{element.ptow}</td>
                                <td>{element.wtow}</td>
                                <td>{element.wyTow}</td>
                                <td><Button variant="outline-info">Edytuj</Button></td>
                                <td><Button variant="outline-info">Usun</Button></td>
                            </tr>

                        ) )
                    }
                    </tbody>
                </Table>
            );
        else if(this.props.typ === "firms")
            return (

                <Table>
                    <thead>
                    <tr>
                        <th> ID Firmy </th>
                        <th> Typ Firmy</th>
                        <th> Nazwa </th>
                        <th>Pelna Nazwa </th>
                        <th> NIP </th>
                        <th> PESEL</th>
                        <th>Adresa</th>
                        <th> Osoba Kontaktowa</th>
                        <th> Telefon</th>
                        <th> Email </th>
                        <th colspan="2" ><Button variant="outline-info" onClick={() => this.add(-1, "dodaj")}>Dodaj Firme</Button></th>
                    </tr>
                    </thead>
                    <tbody>
                    { this.state.typ==="dodaj" ? this.state.render && <Dodawanie typ={this.props.typ}/> : '' }
                    {
                        this.state.firm.map((element: any) => (
                            <tr>
                                <td>{element.idF}</td>
                                <td>{element.tfir}</td>
                                <td>{element.snaz}</td>
                                <td>{element.pnaz}</td>
                                <td>{element.nip}</td>
                                <td>{element.pesel}</td>
                                <td>{element.idAd}</td>
                                <td>{element.osoba}</td>
                                <td>{element.tel}</td>
                                <td>{element.email}</td>
                                <td><Button variant="outline-info">Edytuj</Button></td>
                                <td><Button variant="outline-info">Usun</Button></td>
                            </tr>

                        ) )
                    }
                    </tbody>
                </Table>
            );
        else if(this.props.typ === "mags")
            return (
                <Table>
                    <thead>
                    <tr>
                        <th> ID Magazynu </th>
                        <th> Nazwa </th>
                        <th> Adresa </th>
                        <th> Kierownik magazynu </th>
                        <th> Telefon </th>
                        <th></th>
                        <th colSpan="2"><Button variant="outline-info" onClick={() => this.add(-1, "dodaj")}>Dodaj Magazyn</Button></th>
                    </tr>
                    </thead>
                    <tbody>
                        { this.state.typ==="dodaj" ? this.state.render && <Dodawanie typ={this.props.typ} /> : '' }
                    {
                        this.state.mags.map((element: any) => (
                            <>
                            <tr>
                                <td>{element.idMag}</td>
                                <td>{element.nmag}</td>
                                <td>{element.idAd}</td>
                                <td>{element.kmag}</td>
                                <td>{element.tmag}</td>
                                <td><Button variant="outline-info" onClick={() => this.add(element.idMag, "details")}>Details</Button></td>
                                <td><Button variant="outline-info">Edytuj</Button></td>
                                <td><Button variant="outline-info">Usun</Button></td>
                            </tr>
                            { this.state.idClick === element.idMag && this.state.typ==="details" ? this.state.render && <Details id={element.idMag} typ={this.props.typ}/> : '' }
                            </>
                        ) )
                    }
                    </tbody>
                </Table>
            );
        else if(this.props.typ === "adres")
            return (
                <Table>
                    <thead>
                    <tr>
                        <th> ID Adresy </th>
                        <th> Ulica </th>
                        <th> Budynek </th>
                        <th> Mejscowosc </th>
                        <th> Kod Pocztowy </th>
                        <th> Kraj </th>
                        <th colSpan="2"><Button variant="outline-info" onClick={() => this.add(-1, "dodaj")}>Dodaj Adres</Button></th>
                    </tr>
                    </thead>
                    <tbody>
                    { this.state.typ==="dodaj" ? this.state.render && <Dodawanie typ={this.props.typ} /> : '' }
                    {
                        this.state.adres.map((element: any) => (
                            <tr>
                                <td>{element.idAd}</td>
                                <td>{element.ulica}</td>
                                <td>{element.bud}</td>
                                <td>{element.mej}</td>
                                <td>{element.kod}</td>
                                <td>{element.kraj}</td>
                                <td><Button variant="outline-info" onClick={() => this.add(element.idAd, "update")}>Edytuj</Button></td>
                                <td><Button variant="outline-info" onClick={() => this.add(element.idAd, "delete")}>Usun</Button></td>
                            </tr>

                        ) )
                    }
                    </tbody>
                </Table>
            );
        else if(this.props.typ === "stanmag" )
            return (<>
                    <tr className="bg-light">

                        <th> </th>
                        <th> Towar </th>
                        <th> Numer serijny</th>
                        <th> Ilosc </th>
                        <th></th>

                    </tr>
                    {
                        this.state.stanmag.map((element: any) => (
                            this.props.id === element.idMag ?
                                <tr className="bg-light">
                                    <td></td>
                                    <td>{element.idTow}</td>
                                    <td>{element.serTow}</td>
                                    <td>{element.ilTow}</td>
                                    <td></td>
                                </tr>
                                : ''

                        ) )
                    }
                </>
            );
    }
}

export default TabView;
