import React, {Component} from 'react';
import {Button, Container, Form, Nav, Navbar, Modal} from "react-bootstrap";

import logo from './logo.png'
import {BrowserRouter as Router, Switch, Route} from "react-router-dom";
import Home from "../Pages/Home";
import Dokumenty from "../Pages/Dokumenty";
import Dane from "../Pages/Dane";
import Login from "../Components/Login"

class Header extends Component {
    constructor(props) {
        super(props);
        this.state = {
            login : false,
            renderhome: true,
            renderdok: false,
            renderdane: false,
            renderlog: false,
            typ : "home"
        };
    }
    headerClick(typ)
    {
        this.setState({typ: typ});
        if(typ === "dok")
            this.setState({renderdok : true});
        else if(typ === "dane")
            this.setState({renderdane : true});
        else if (typ === "home")
            this.setState({renderhome : true});
        else if (typ === "login")
            this.setState({renderlog : true});

    }
    render() {

        return (
            <>
            <Navbar fixed="top" collapseOnSelect expand="md" bg="dark" variant="dark">
                <Container>
                    <Navbar.Brand onClick={() => this.headerClick("home")}>
                        <img
                            src={logo}
                            height="30"
                            width="30"
                            className="d-inline-block align-top"
                            alt="logo"
                        /> Magazyn
                    </Navbar.Brand>
                    <Navbar.Toggle aria-controls="responsive-navbar-nav"/>
                    <Navbar.Collapse id="responsive-navbar-nav">
                        <Nav className="mr-auto">
                            <Nav.Link onClick={() => this.headerClick("dok")}>Dokumenty</Nav.Link>
                            <Nav.Link onClick={() => this.headerClick("dane")}>Dane</Nav.Link>
                        </Nav>
                        <Form inline>
                            <Button variant="outline-info" onClick={() => this.headerClick("login")} >Logowanie</Button>
                        </Form>
                    </Navbar.Collapse>
                </Container>
            </Navbar>
                { this.state.typ==="dok" ? this.state.renderdok && <Dokumenty login={this.state.login}/> : '' }
                { this.state.typ==="home" ? this.state.renderhome && <Home login={this.state.login}/> : '' }
                { this.state.typ==="dane" ? this.state.renderdane && <Dane login={this.state.login}/> : '' }
                {this.state.typ==="login" ? this.state.renderlog && <Login /> : ''}
                {/* <Router>
            <Switch>
                <Route exact path="/" children={<Home login={this.state.login}/>}/>
                <Route exact path="/dokumenty" component={<Dokumenty login={this.state.login}/>}/>
                <Route exact path="/dane" component={<Dane login={this.state.login}/>}/>
            </Switch>
        </Router>*/}
                
        </>
        );
    }
}

export default Header;
