import React, {Component} from 'react';
import {Button, Form, Container} from "react-bootstrap";

class Login extends Component {
    constructor(props) {
        super(props);
        this.state = {
            login: null,
            haslo: null
        };
    }
    handleSubmin = (event) => {
        event.preventDefault();
    }
    handleChangeLogin = (event) => {
        event.preventDefault();
        this.setState(
            {[event.target.name] : event.target.value}
        )
    }
    render() {
        const {login} = this.state
        return (
            <Container>
                <p>{login}</p>
            <div className="login-in-page">
                <Form onSubmin={this.handleSubmit} className="log">
                    <Form.Label>Login</Form.Label>
                    <Form.Control type="input" placeholder="Login" name="login" onChange={this.handleChangeLogin}/>
                    <Form.Label>Haslo</Form.Label>
                    <Form.Control type="input" placeholder="Haslo" name="haslo"/>
                    <Button variant="outline-info" type="submit" className="log mt-3"> LogIn </Button>
                </Form>
            </div>
            </Container>
        );
    }
}

export default Login;
