import React, {Component} from 'react';
import {Table, Button} from "react-bootstrap";
import Moment from 'moment';
import axios from 'axios';
import Details from "../Pages/Details";

class TabView extends Component {
    constructor(props) {
        super(props);
        this.state = {
            mags : [],
            dokmag: [],
            dok : [],
            tow :[],
            firm : [],
            idClick : -1
        };
    }
    state= { render: false }
    add(id)
    {
        this.state.idClick = id;
        this.setState({render : !this.state.render})
    }
    componentDidMount() {
        axios({
            method: 'get',
            url: 'http://localhost:8081/dokmag/all',
        }).then((response) => {
            this.setState({dokmag: response.data})
            console.log(response.data)

        }).catch((error) => {
            console.log(error)
        });

        axios({
            method: 'get',
            url: 'http://localhost:8081/firmy/all',
        }).then((response) => {
            this.setState({firm: response.data})
            console.log(response.data)

        }).catch((error) => {
            console.log(error)
        });

        axios({
            method: 'get',
            url: 'http://localhost:8081/tow/all',
        }).then((response) => {
            this.setState({tow: response.data})
            console.log(response.data)

        }).catch((error) => {
            console.log(error)
        });
        axios({
            method: 'get',
            url: 'http://localhost:8081/dok/all',
        }).then((response) => {
            this.setState({dok: response.data})
            console.log(response.data)

        }).catch((error) => {
            console.log(error)
        });
        axios({
            method: 'get',
            url: 'http://localhost:8081/mag/all',
        }).then((response) => {
            this.setState({mags: response.data})
            console.log(response.data)

        }).catch((error) => {
            console.log(error)
        });
    }
    render() {
        if(this.props.typ === "doks")
        return (
            <>
            <Table className="table">
                <thead>
                <tr>
                    <th>No</th>
                    <th>Magazyn</th>
                    <th>Typ Dokumentu</th>
                    <th>No Dokumentu</th>
                    <th>Firma</th>
                    <th>Data</th>
                    <th colSpan="2"><Button variant="outline-info">Dodaj Dokument</Button></th>
                </tr>
                </thead>
                <tbody >
                {
                    this.state.dokmag.map((element: any) => (
                        <>
                        <tr>
                            <td>{element.idDok}</td>
                            <td>{element.idMag}</td>
                            <td>{element.tdok}</td>
                            <td>{element.ndok}</td>
                            <td>{element.idF}</td>
                            <td>{Moment(element.data).format("DD-MM-YYYY hh:mm")}</td>
                            <td><Button variant="outline-info" onClick={() => this.add(element.idDok)}>Details</Button></td>

                        </tr>
                          { this.state.idClick == element.idDok ? this.state.render && <Details id={element.idDok}/> : ''
                        }

                        </>

                    ) )
                }
                </tbody>
            </Table>

        </>
        );
        else if(this.props.typ === "dok" )
            return (<>
                    <tr className="bg-light">

                        <th> </th>
                        <th> Kod kreskowy </th>
                        <th> Producent </th>
                        <th> Waga(kg)</th>
                        <th>Wymiary(cm)</th>
                        <th colSpan="2"><Button variant="outline-info">Dodaj Pozycje</Button></th>
                    </tr>
                      {
                        this.state.dok.map((element: any) => (
                         this.props.id == element.idDok ?
                          <tr className="bg-light">
                           <td></td>
                          <td>{element.lpoz}</td>
                          <td>{element.idTow}</td>
                          <td>{element.ilTow}</td>
                          <td>{element.ilTow}</td>
                          <td><Button variant="outline-info">Edytuj</Button></td>
                          <td><Button variant="outline-info">Usun</Button></td>
                          </tr>
                            : ''

                        ) )
                    }
                </>
            );
        else if(this.props.typ === "tow")
            return (
                <Table>
                    <thead>
                    <tr>
                        <th> ID Towaru</th>
                        <th> Nazwa</th>
                        <th> Kod kreskowy </th>
                        <th> Producent </th>
                        <th> Waga(kg)</th>
                        <th>Wymiary(cm)</th>
                        <th colSpan="2"><Button variant="outline-info">Dodaj Towar</Button></th>
                    </tr>
                    </thead>
                    <tbody>
                    {
                        this.state.tow.map((element: any) => (
                            <tr>
                                <td>{element.idTow}</td>
                                <td>{element.ntow}</td>
                                <td>{element.ktow}</td>
                                <td>{element.ptow}</td>
                                <td>{element.wtow}</td>
                                <td>{element.wyTow}</td>
                                <td><Button variant="outline-info">Edytuj</Button></td>
                                <td><Button variant="outline-info">Usun</Button></td>
                            </tr>

                        ) )
                    }
                    </tbody>
                </Table>
            );
        else if(this.props.typ === "firms")
            return (

                <Table>
                    <thead>
                    <tr>
                        <th> ID Firmy </th>
                        <th> Typ Firmy</th>
                        <th> Nazwa </th>
                        <th>Pelna Nazwa </th>
                        <th> NIP </th>
                        <th> PESEL</th>
                        <th>Adresa</th>
                        <th> Osoba Kontaktowa</th>
                        <th> Telefon</th>
                        <th> Email </th>
                        <th colspan="2" ><Button variant="outline-info">Dodaj Firme</Button></th>
                    </tr>
                    </thead>
                    <tbody>
                    {
                        this.state.firm.map((element: any) => (
                            <tr>
                                <td>{element.idF}</td>
                                <td>{element.tfir}</td>
                                <td>{element.snaz}</td>
                                <td>{element.pnaz}</td>
                                <td>{element.nip}</td>
                                <td>{element.pesel}</td>
                                <td>{element.idAd}</td>
                                <td>{element.osoba}</td>
                                <td>{element.tel}</td>
                                <td>{element.email}</td>
                                <td><Button variant="outline-info">Edytuj</Button></td>
                                <td><Button variant="outline-info">Usun</Button></td>
                            </tr>

                        ) )
                    }
                    </tbody>
                </Table>
            );
        else if(this.props.typ === "mags")
            return (
                <Table>
                    <thead>
                    <tr>
                        <th> ID Magazynu </th>
                        <th> Nazwa </th>
                        <th> Adresa </th>
                        <th> Kierownik magazynu </th>
                        <th> Telefon </th>
                        <th colSpan="2"><Button variant="outline-info">Dodaj Magazyn</Button></th>
                    </tr>
                    </thead>
                    <tbody>
                    {
                        this.state.mags.map((element: any) => (
                            <tr>
                                <td>{element.idMag}</td>
                                <td>{element.nmag}</td>
                                <td>{element.idAd}</td>
                                <td>{element.kmag}</td>
                                <td>{element.tmag}</td>
                                <td><Button variant="outline-info">Edytuj</Button></td>
                                <td><Button variant="outline-info">Usun</Button></td>
                            </tr>

                        ) )
                    }
                    </tbody>
                </Table>
            );
    }
}

export default TabView;
