import React, {Component} from 'react';
import {Table, Button} from "react-bootstrap";
import Moment from 'moment';
import axios from 'axios';

class TabView extends Component {
    constructor(props) {
        super(props);
        this.state = {
            dokmag: [],
            dok : [],
            tow :[]
        };
    }
    componentDidMount() {
        axios({
            method: 'get',
            url: 'http://localhost:8081/dokmag/all',
        }).then((response) => {
            this.setState({dokmag: response.data})
            console.log(response.data)

        }).catch((error) => {
            console.log(error)
        });
        axios({
            method: 'get',
            url: 'http://localhost:8081/tow/all',
        }).then((response) => {
            this.setState({tow: response.data})
            console.log(response.data)

        }).catch((error) => {
            console.log(error)
        });
        axios({
            method: 'get',
            url: 'http://localhost:8081/dok/all',
        }).then((response) => {
            this.setState({dok: response.data})
            console.log(response.data)

        }).catch((error) => {
            console.log(error)
        });
    }
    render() {
        if(this.props.typ == "doks")
        return (
            <Table className="table">
                <thead>
                <tr>
                    <th>No</th>
                    <th>Magazyn</th>
                    <th>Typ Dokumentu</th>
                    <th>No Dokumentu</th>
                    <th>Firma</th>
                    <th>Data</th>
                </tr>
                </thead>
                <tbody >
                {
                    this.state.dokmag.map((element: any) => (
                        <tr>
                            <td>{element.idDok}</td>
                            <td>{element.idMag}</td>
                            <td>{element.tdok}</td>
                            <td>{element.ndok}</td>
                            <td>{element.idF}</td>
                            <td>{Moment(element.data).format("DD-MM-YYYY hh:mm")}</td>
                        </tr>

                    ) )
                }
                </tbody>
            </Table>
        );
        else if(this.props.typ == "tow")
            return (
                <Table>
                    <thead>
                    <tr>
                        <th> ID Towaru</th>
                        <th> Nazwa</th>
                        <th> Kod kreskowy </th>
                        <th> Producent </th>
                        <th> Waga(kg)</th>
                        <th>Wymiary(cm)</th>
                        <th></th>
                        <th></th>
                    </tr>
                    </thead>
                    <tbody>
                    {
                        this.state.tow.map((element: any) => (
                            <tr>
                                <td>{element.idTow}</td>
                                <td>{element.ntow}</td>
                                <td>{element.ktow}</td>
                                <td>{element.ptow}</td>
                                <td>{element.wtow}</td>
                                <td>{element.wyTow}</td>
                                <td><Button>Edytuj</Button></td>
                                <td>Usun</td>
                            </tr>

                        ) )
                    }
                    </tbody>
                </Table>
            );
    }
}

export default TabView;
