import React, {Component} from 'react';
import {Button, Tab,} from "react-bootstrap";

class Adres extends Component {
    constructor(props) {
        super(props);
        this.state = {
            adres : [],
            stanmag: [],
            idClick : -1,
            render: false,
            typ : ""
        };
    }
    add(id, typ)
    {
        this.state.idClick = id;
        this.state.typ = typ;
        this.setState({render : !this.state.render})
    }
    render() {
        return (
            <Table>
                <thead>
                <tr>
                    <th> ID Adresy </th>
                    <th> Ulica </th>
                    <th> Budynek </th>
                    <th> Mejscowosc </th>
                    <th> Kod Pocztowy </th>
                    <th> Kraj </th>
                    <th colSpan="2"><Button variant="outline-info" onClick={() => this.add(-1, "dodaj")}>Dodaj Adres</Button></th>
                </tr>
                </thead>
                <tbody>
                { this.state.typ==="dodaj" ? this.state.render && <Dodawanie typ={this.props.typ} /> : '' }
                {
                    this.state.adres.map((element: any) => (
                        <tr>
                            <td>{element.idAd}</td>
                            <td>{element.ulica}</td>
                            <td>{element.bud}</td>
                            <td>{element.mej}</td>
                            <td>{element.kod}</td>
                            <td>{element.kraj}</td>
                            <td><Button variant="outline-info" onClick={() => this.add(element.idAd, "update")}>Edytuj</Button></td>
                            <td><Button variant="outline-info" onClick={() => this.add(element.idAd, "delete")}>Usun</Button></td>
                        </tr>

                    ) )
                }
                </tbody>
            </Table>
        );
    }
}

export default Adres;
